$(function () {
    var Lang = 'vi';
    var dateRangePickerLocale = Lang == "en"?{
            format: "DD-MM-YYYY",
            customRangeLabel: "Custom",
            applyLabel: "Select",
            cancelLabel: "Delete",
    }:{
            format: "DD-MM-YYYY",
            customRangeLabel: "Tùy Chọn",
            applyLabel: "Chọn",
            cancelLabel: "Xóa",
    };
  
    var begin = moment(moment().startOf("month")).format("YYYY-MM-DD");
    var end = moment(moment().endOf("month")).format("YYYY-MM-DD");
    $("#dateBegin").val(begin);
    $("#dateEnd").val(end);
    $('#daterange').daterangepicker({
        ranges   : {
            'Từ trước đến nay'  : ['', ''],
            'Hôm nay'       : [moment(), moment()],
            'Hôm qua'   : [moment().subtract(1, 'days'), moment().subtract(1, 'days')],
            '7 Ngày qua' : [moment().subtract(6, 'days'), moment()],
            '30 ngày trước': [moment().subtract(29, 'days'), moment()],
            'Tháng này'  : [moment().startOf('month'), moment().endOf('month')],
            'Tháng trước'  : [moment().subtract(1, 'month').startOf('month'), moment().subtract(1, 'month').endOf('month')]
           
        },
        locale: dateRangePickerLocale,
        startDate: moment().subtract(29, 'days'),
        endDate  : moment(),
        language:Lang,
        },
        function (start, end) {
         if(start._isValid==false||end._isValid==false){
            $('#daterange').attr('readonly',true);
            $("#dateBegin").val("");
            $("#dateEnd").val("");
         }else{
            $("#dateBegin").val(start.format("YYYY-MM-DD"));
            $("#dateEnd").val(end.format("YYYY-MM-DD"));
            $('#daterange').attr('readonly',false);
         }
         
        }
      )

});
function _AjaxDelete(dataOject,url){
    var isBoool = false;
    $.ajax({
        url:url,
        type:'POST',
        data:dataOject,
        dataType:'JSON',
        async:false,
        success:function(data){
            if(data.statusBoolen){
                isBoool= true;
            }else{
                isBoool= false;
            }
        },error:function (error) {
            console.log(error);
        }
    });
    return isBoool;
}


